For.DomContent:

var items = $items.value.split(/[\r\n]+/);
var specs = [];

for (var i in items) {
    var label = items[i];
    var selected = false;
    var type = "";
    var checked = false;

    if (label.match(/^#/)) {
        selected = true;
        label = label.substring(1);
    }
    if (label.match(/^\[\]/)) {
        label = label.substring(2);
        type = "checkbox";
    } else if (label.match(/^\[\*\]/)) {
        label = label.substring(3);
        type = "checkbox";
        checked = true;
    } else if (label.match(/^\(\)/)) {
        label = label.substring(2);
        type = "radio";
    } else if (label.match(/^\(\*\)/)) {
        label = label.substring(3);
        type = "radio";
        checked = true;
    }

    specs.push(
        {
            _name: "listitem",
            _uri: "http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul",
            label: label,
            type: type,
            checked: checked,
            selected: selected
        }
    );
}

F.newDOMFragment(specs);

