var fragment = null;

try {
    var a = F.parseTextArray($content.value);

    var specs = [];

    var cols = {
        _name: "treecols",
        _uri: PencilNamespaces.xul,
        _children: []
    };
    var rows = {
        _name: "treechildren",
        _uri: PencilNamespaces.xul,
        _children: []
    };

    if (a.length > 0) {
        var headers = a[0];
        var lastWidth = 0;
        for (var i = 0; i < headers.length; i ++) {
            var header = headers[i];
            if (i > 0) {
                cols._children.push({
                            _name: "splitter",
                            _url: PencilNamespaces.xul,
                            class: "tree-splitter"
                        });
            }

            var label = header;
            var type = "";
            if (header.match(/^\[\](.*)/)) {
                label = RegExp.$1;
                type = "checkbox";
            } else if (header.match(/^==(.*)/)) {
                label = RegExp.$1;
                type = "progressmeter";
            }


            //depends $h0 $h1 $h2 $h3 $h4 $h5 $h6 $h7 $h8 $h9 $h10 $h11

            var h = {
                        _name: "treecol",
                        _url: PencilNamespaces.xul,
                        id: ("" + new Date().getTime() + Math.round(Math.random() * 1000)),
                        flex: (i == headers.length - 1) ? 1 : 0,
                        label: label,
                        type: type
                    };

            if (i < headers.length - 1) {
                h.width = properties["h" + i].x - lastWidth;
            }

            cols._children.push(h);
            lastWidth = properties["h" + i].x;
        }
    }
    for (var i = 1; i < a.length; i ++) {
        var row = {
            _name: "treerow",
            _uri: PencilNamespaces.xul,
            _children: []
        }
        var treeitem = {
            _name: "treeitem",
            _uri: PencilNamespaces.xul,
            _children: [row]
        };
        rows._children.push(treeitem);

        var cells = a[i];
        for (var j = 0; j < cells.length; j ++) {
            var cell = cells[j];

            row._children.push({
                _name: "treecell",
                _uri: PencilNamespaces.xul,
                label: cell,
                value: "50%",
                editable: true
            });
        }
    }
    specs.push(cols);
    specs.push(rows);

    fragment = F.newDOMFragment(specs);
} catch (e) {
    Console.dumpError(e);
}

fragment;

