var a = 32;
var padding = 4;
size = a - 2 * padding;
var arr = new Array();
var i = 1;
var space = 5;

//close button
if($withCloseButton.value)
{
    arr.push(skline($box.w - size - padding + 4, padding + 4, $box.w - padding - 4, a - padding - 4));
    arr.push(skline($box.w - padding - 4, padding + 4, $box.w - padding - size + 4, a - padding - 4));
    i++;
}

//restore button
if($withRestoreButton.value)
{
    arr.push(skline($box.w - i * (size + padding) + space, padding + space, $box.w - (i - 1)*(size + padding) - padding - space, padding + space));
    arr.push(sklineTo($box.w - (i - 1)*(size + padding) - padding - space, a - padding - space));
    arr.push(sklineTo($box.w - i * (size + padding) + space, a - padding - space));
    arr.push(z);
    i++;
}

//min button
if($withMinButton.value)
{
    arr.push(skline($box.w - i * (size + padding) + space, a - padding - space,  $box.w - (i - 1)*(size + padding) - padding - space, a - padding - space));
}
arr

