    $dayFillColor.hollowed(date["value"] == $pickDay.value ? 0.77 : 0.97);
}

For.Color:
var color;
if ($start.value == "monday") {
    color = Color.fromString("#4166B5");
} else {
    color = Color.fromString("#000");
}

var date = getCalendarDate($monthContent.toString(), 41, $start.value == "sunday" ? true : false);
if (date["disabled"]) {
    color.hollowed(0.5);
} else {
    color.hollowed(0);
}

